INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (115, 'Active', 'The Enlightenment,Root.History.Modern_History.The_Enlightenment', 'Intermediate', 'Offline', 'Fundamentals of Scalable Data Science', 'Apache Spark is the de-facto standard for large scale data processing. This is the first course of a series of courses towards the IBM Advanced Data Science Specialization. We strongly believe that is is crucial for success to start learning a scalable data science platform since memory and CPU constraints are to most limiting factors when it comes to building advanced machine learning models.
