INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (130, 'Active', 'Root,Root', 'Low', 'Live', 'Professional Responsibility and Ethics for Accountants', 'Accountants perform a wide variety of activities which include, but are not limited to, accounting, auditing, tax, management consulting, and financial management.  However, a professional accountant’s responsibility is not exclusively to satisfy the needs of an employer or professional services client.  One of the distinguishing characteristics of the accounting profession is its acceptance of the responsibility to act in the public interest. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (131, 'Inactive', 'Electricity and Magnetism,Root.Science.Physics.Applied_Physics.Electricity_and_Magnetism', 'Beginner', 'Live', 'Advanced Converter Control Techniques', 'This course covers advanced converter control techniques, including averaged-switch modeling and Spice simulations, modeling and design of peak current mode and average current mode controlled converters, as well as an introduction to control of single-phase ac grid tied rectifiers and inverters. Design and simulation examples include wide bandwidth point-of-load voltage regulators, low-harmonic power-factor-correction rectifiers, and grid-tied inverters for solar photovoltaic power systems. Upon completion of the course, you will be able to model, design control loops, and simulate state-of-the-art pulse-width modulated (PWM) dc-dc converters, dc-ac inverters, ac-dc rectifiers, and other power electronics systems. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (132, 'Active', 'Single Variable Calculus,Root.Mathematics.Calculus.Single_Variable_Calculus', 'Absolute Beginner', 'Offline', 'Маркетинг. Часть 1. Основные категории, принципы и подходы', 'Маркетинг – система ведения производственно-коммерческой деятельности предприятия с ориентацией на рынок, главными элементами которой являются 1) потребитель, 2) конкуренты. В курсе рассмотрены аспекты маркетинга как предприятий, работающих с потребителями товаров широкого потребления (В2С рынки), так и предприятий, клиентами которых являются другие предприятия (В2В рынки). Структура курса включает в себя изучение аспектов поведения потребителей на В2С и В2В рынках, методов маркетинговых исследований потребителей и конкурентов, управления маркетингом и оценки его эффективности. В разработке курса приняли участие компании «CML - Центр компьютерного инжиниринга СПбПУ», 3М, SWED-MOBIL, DDC.Lab, Media Price. В заданиях были использованы данные компании Mediascope, материалы маркетингового клуба КЛУМБА|ПОЛИТЕХ.
