INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (166, 'Draft', 'The Industrial Revolution,Root.History.Modern_History.The_Industrial_Revolution', 'High', 'Offline', 'Introduction to TensorFlow for Artificial Intelligence, Machine Learning, and Deep Learning', 'If you are a software developer who wants to build scalable AI-powered algorithms, you need to understand how to use the tools to build them. This course is part of the upcoming Machine Learning in Tensorflow Specialization and will teach you best practices for using TensorFlow, a popular open-source framework for machine learning. 
