INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (168, 'Inactive', 'Higher Order Differential Equations,Root.Mathematics.Differential_Equations.Higher_Order_Differential_Equations', 'Absolute Beginner', 'Online', 'Hands-on Text Mining and Analytics', 'This course provides an unique opportunity for you to learn key components of text mining and analytics aided by the real world datasets and the text mining toolkit written in Java. Hands-on experience in core text mining techniques including text preprocessing, sentiment analysis, and topic modeling help learners be trained to be a competent data scientists. 
