INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (215, 'Inactive', 'Science,Root.Science', 'Absolute Beginner', 'Live', 'Cybersecurity and Mobility', 'This course is for you if you are interested in transitioning toward a managerial role in cybersecurity and mobility. Through interviews with industry experts in this area, you will be able to analyze innovations powering the rapid spread of information technology and how they present new challenges for protecting data. For example, mobile devices increase convenience but often bypass traditional security measures. After this course, you will be able to describe how the nature of the threat evolves, as culprits employ a burgeoning set of sophisticated tools to take advantage of our growing reliance on networks for critical-data exchange.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (216, 'Inactive', 'Electricity and Magnetism,Root.Science.Physics.Applied_Physics.Electricity_and_Magnetism', 'Introductory', 'Offline', 'Easing the burden of obesity, diabetes and cardiovascular disease', 'This course is multidisciplinary in nature, and aims to equip the global audience of interested lay people, people with chronic disease, public health researchers, health clinicians, students, administrators, and researchers to reflect on the overall impact of the burden of chronic disease . It shows how all chronic diseases (obesity, diabetes, cardiovascular disease, chronic obstructive pulmonary disease, and cancer) are related by a set of common causes, and that such diseases should be tackled, not individually, but as part of a complex system, with interrelated contributing factors. These factors are genetic, environmental, psychological, economic, social, developmental, and media related. 
