INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (255, 'Active', 'Mathematics,Root.Mathematics', 'Beginner', 'Offline', 'Literacy Teaching and Learning: Aims, Approaches and Pedagogies', 'This course opens with an exploration of the social context and aims of literacy teaching and learning. It goes on to describe a range of historical and contemporary approaches to literacy pedagogy, including didactic, authentic, functional, and critical approaches. The course takes has a ''Multiliteracies'' perspective, which aims to expand the definition of literacy to encompass today''s multimodal communications, and the diversity of literacies across different social and culltural contexts. A Multiliteracies approach also suggests a broad range of activity types—experiential, conceptual, analytical and critical.
