INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (259, 'Active', 'Number Theory,Root.Mathematics.Number_Theory', 'Medium', 'Live', 'Introduction to Complex Analysis', 'This course provides an introduction to complex analysis which is the theory of complex functions of a complex variable. We will start by introducing the complex plane, along with the algebra and geometry of complex numbers, and then we will make our way via differentiation, integration, complex dynamics, power series representation and Laurent series into territories at the edge of what is known today. Each module consists of five video lectures with embedded quizzes, followed by an electronically graded homework assignment.  Additionally, modules 1, 3, and 5 also contain a peer assessment. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (260, 'Inactive', 'The Industrial Revolution,Root.History.Modern_History.The_Industrial_Revolution', 'Beginner', 'Offline', 'Religious Transformation in Early China: the Period of Division', 'This sequence of four courses will propose a multi-disciplinary approach to the study of Chinese cultural history conceived of as a succession of modes of rationality (philosophical, bureaucratic, and economic). The focus will be on the moments of paradigm shift from one mode of rationality to another. For each of these moments, cultural facts and artifacts—thought, literature, ritual—will be examined in relationship to changing social, political, and economic systems.
