INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (264, 'Inactive', 'Single Variable Calculus,Root.Mathematics.Calculus.Single_Variable_Calculus', 'Intermediate', 'Live', 'Childbirth: A Global Perspective', 'This course will review challenges for maternal and newborn health in the developing world, where a great many women and babies are suffering from complications during pregnancy, childbirth, and the days following birth. Themes covered include the epidemiology of maternal and newborn mortality and morbidity, relevant issues for the global health workforce, community-based interventions to improve maternal and newborn health and survival, and sociocultural dynamics surrounding birth.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (265, 'Active', 'Science,Root.Science', 'Medium', 'Offline', 'Designing Your Personal Weight Loss Plan', 'This 5 week course will guide learners through the essential steps in planning an individualized weight loss program. There is no guarantee of weight loss through completing the course; learners will have the framework and essential components for an evidence-based weight loss program. This course is intended for healthy adults who do not have any chronic disease such as diabetes, high blood pressure, heart disease, lung disease or any others. In addition, this course does not provide information for people who have food allergies or intolerances.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (266, 'Active', 'Astronomy,Root.Science.Astronomy', 'Beginner', 'Offline', 'Hôtel “De l''étoile” - a hotel in crisis? ', 'In this case study based exercise, you will apply the knowledge you have gained about distribution, revenue and demand management to a ‘real’ world case – Hotel “De l''étoile” - a prestigious upmarket internationally branded property in Paris that to the outside world seems to be doing well but is in fact unprofitable for its owner.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (267, 'Draft', 'Condensed Matter Physics,Root.Science.Physics.Applied_Physics.Condensed_Matter_Physics', 'Advanced', 'Offline', 'Formal Financial Accounting', 'This course builds upon what you learned in Financial Accounting: Foundations and Financial Accounting: Advanced Topics and introduces you to formal foundations of financial accounting. The main purpose of the course is to familiarize you with technical terms in financial accounting, such as debit, credit and T-account. While knowledge on technical aspects of financial accounting is not necessary to understand and interpret financial statements, this knowledge is critical to be able to understand advanced topics in accounting. Formal Financial Accounting is a prerequisite course for the University of Illinois'' iMSA program and the terminology we will cover will be used throughout the courses in iMSA program.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (268, 'Draft', 'Science,Root.Science', 'Advanced', 'Offline', 'Automated Reasoning: Symbolic Model Checking', 'This course presents how properties of acting systems and programs can be verified automatically. The basic notion is a transition system: any system that can be described by states and steps. We present how in CTL (computation tree logic) properties like reachability can be described.
