INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (279, 'Inactive', 'Ordinary Differential Equations,Root.Mathematics.Differential_Equations.Ordinary_Differential_Equations', 'Advanced', 'Offline', 'Теория вероятностей - наука о случайности', 'Курс познакомит с основными правилами исчисления вероятностей, обращая внимание на базовые идеи и концепции, научит решать вероятностные задачи, пользуясь формальным аппаратом. Курс позволит освоить элементарные вероятностные методы и применять их в быту и профессиональной деятельности, будет способствовать развитию рационального мышления и способности выражать мысли в математической форме.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (280, 'Active', 'Hellenistic Period,Root.History.Ancient_History.Hellenistic_Period', 'High', 'Live', 'Mountains 101', 'Mountains 101­­ is a broad and integrated overview of the mountain world. This 12-lesson course covers an interdisciplinary field of study focusing on the physical, biological, and human dimensions of mountain places in Alberta, Canada, and around the world. Specifically, we''ll study the geological origins of mountains, how they’re built-up and worn-down over time; we’ll learn about their importance for biodiversity and water cycles, globally and locally; we’ll explore their cultural significance to societies around the globe, and how that relationship has evolved over time; and we’ll learn how mountains are used, how they’re protected, and how today they’re experiencing rapid change in a warming climate. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (281, 'Active', 'Ancient History,Root.History.Ancient_History', 'Advanced', 'Offline', 'Basics of Extracellular Vesicles', 'This course aims to provide the basic knowledge about extracellular vesicles (EV) a generic term including exosomes, microvesicles, microparticles, ectosomes, oncosomes, prostasomes, and many others. It covers areas such as EV history, nomenclature, biogenesis, EV cargo as well as the release and uptake mechanisms, collection and processing prior to isolation, different isolation methods, characterization and quantification techniques.
