INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (350, 'Draft', 'Mathematics,Root.Mathematics', 'Advanced', 'Offline', 'RPA Lifecycle: Deployment and Maintenance', 'Robotic Process Automation (or RPA) implementation is conducted over multiple critical phases. In the Discovery phase, you identify the business processes beneficial for automation. In the Design phase, you create an RPA plan for automating them. In the Development and Testing phase, you execute the RPA plan and develop bots, testing them thoroughly during development.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (351, 'Draft', 'The Digital Age,Root.History.Modern_History.The_Industrial_Revolution.The_Digital_Age', 'Introductory', 'Offline', 'Calculus: Single Variable Part 4 - Applications', 'Calculus is one of the grandest achievements of human thought, explaining everything from planetary orbits to the optimal size of a city to the periodicity of a heartbeat. This brisk course covers the core ideas of single-variable Calculus with emphases on conceptual understanding and applications. The course is ideal for students beginning in the engineering, physical, and social sciences. Distinguishing features of the course include: 1) the introduction and use of Taylor series and approximations from the beginning; 2) a novel synthesis of discrete and continuous forms of Calculus; 3) an emphasis on the conceptual over the computational; and 4) a clear, dynamic, unified approach.
