INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (395, 'Draft', 'Ancient History,Root.History.Ancient_History', 'High', 'Offline', 'Bajemos la temperatura: De la ciencia climática a la acción', 'Todas las regiones del mundo se enfrentan a vulnerabilidades concretas ante el cambio climático y tienen diversas oportunidades de mitigar los efectos y aumentar su resiliencia en el siglo XXI. La comunidad internacional ha expresado su intención de actuar al respecto mediante el Acuerdo de París en la 21ª Conferencia de las Partes en la Convención Marco de las Naciones Unidas sobre el Cambio Climático. De hecho, décadas de avances en desarrollo se ven amenazadas si no se adoptan medidas contra el cambio climático, lo que significa que nos encontramos en un momento de «ahora o nunca». Este curso presenta las pruebas científicas más recientes sobre el cambio climático, explica los distintos efectos de alcance regional y difunde estrategias de acción contra el cambio climático, así como oportunidades para que pueda tomar medidas en relación con el cambio climático.
