INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (445, 'Active', 'The Digital Age,Root.History.Modern_History.The_Industrial_Revolution.The_Digital_Age', 'Beginner', 'Offline', 'Авторские школы ', 'В курсе рассматриваются кейсы авторских школ в российском образовании. Материалы содержат видео-интервью с директорами и управленческими  командами, которые выступили  в качестве организаторов введения инновационных образовательных технологий. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (416, 'Inactive', 'The Industrial Revolution,Root.History.Modern_History.The_Industrial_Revolution', 'High', 'Online', 'Teaching the Violin and Viola: Creating a Healthy Foundation', 'Join us as we explore the fundamental principles and early stages of teaching violin and viola through a series of engaging video lectures and lesson demonstrations, including: 1) Master classes with some of the world’s top string pedagogues, 2) Individual lesson demos of teaching violin/viola set up, left and right hand technique and pieces from the early violin/viola repertoire, 3) Group lesson demos of teaching music theory, ear training and fun activities that encourage good playing habits, and 4) A “field trip” to the violin shop, where we will outfit a new beginning student with a proper instrument and bow. You’ll also engage in a series of discussions and reflections with colleagues and peers across the globe. Upon completion of this course, we hope you’ll return to your studio with confidence, a new curriculum, and a renewed approach to sharing with your students the joyful experience of making beautiful music.', '{}', 'http://www.example.com/', 7, 399, 22, 'http://www.outdoor-photos.com/_photo/2569091.jpg', '{"Uniformity fatalities vernacular chancelike increased cooking fencer terrafirma produce ensure importance north","Heroes gashed twist","Substituted fullrigged learned eighteen peril streets alluring scarce"}', 'TtVaVCaHF_Ac', '{"Pierce marking","Pardon nails apprehensiveness peculi","Since front frosts likeness","Faithful fabled","Peace ocean caramba"}', '08:45:00-06', '20:00:00-06', '2024-01-25 00:53:59+00', '2039-08-21 01:05:23+00', 'abe63a3e-f71a-4f8c-960b-2dd565062bd0');
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (417, 'Inactive', 'Condensed Matter Physics,Root.Science.Physics.Applied_Physics.Condensed_Matter_Physics', 'Introductory', 'Online', 'Wind Energy', 'How tall is a modern wind turbine and how can it possibly generate power from the wind? This course gives an overview of key aspects in wind energy engineering. Whether you are looking for general insight in this green technology or your ambition is to pursue a career in wind energy engineering, ''Wind Energy'' is an excellent starting point. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (418, 'Active', 'Late Antiquities,Root.History.Ancient_History.Late_Antiquities', 'High', 'Offline', 'Write Like Mozart: An Introduction to Classical Music Composition', 'This course introduces students to strategies for style writing of common practice European art music.  The issues of harmonic progression, voice leading, and texture are addressed in addition to relevant compositional concepts like repetition, variation, and elaboration.  The course aims to offer a creative space even within the restrictions of stylistic emulation.', '{"Обучение на размеченных данных","Privacy in the USA","Philosophy, Science and Religion: Philosophy and Religion","Arts and Heritage Management"}', 'Oakby', 1, 999, 28, 'https://facts.net/wp-content/uploads/2020/02/Van-Gogh-Starry-Night.jpg', '{"Blindly winding certainly oriental torments","Rains goring sense leaves","Shrieked passiveness candles pride pronouncing whisper sensibly habitants height","Infatuated superior exactness flogged plenty poker sailed christians","Shuttle feeding distinct hootings uncivilised inelegant without sucklings","Perishing confidently","Consecutive valuable dream tiled","Winds fixes bowed warriors dusky migratory mutter","Stopped peter storm cedar surrender draughtsmen managed treated final fishermen","Elders yarns written skrimshander scale butterfly shrouded history"}', 'WLMAItCMC_Tp', '{"Foreseen walks famed problem","Whereto continent pulverise creepingly","Impetuously pardon retribution","Solace outstrip","Disheartening indefinite"}', '12:30:00-07', '17:30:00-07', '2045-07-18 03:25:10+00', '2049-11-06 05:46:38+00', '59ec1f05-fb4b-436e-a54d-1c4368dfe69f');
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (419, 'Active', 'Hellenistic Period,Root.History.Ancient_History.Hellenistic_Period', 'Intermediate', 'Online', '真格—北大在线创业课堂', '《真格北大创业课》是真格基金与北京大学国家发展研究院首次联合开发的中文“慕课”（MOOC, Massive Open Online Course，大型公开在线课程)。课程以创业为主题，共分为三个部分：创业前的准备工作，创业中的要点，以及融资、众筹的技巧等。此课程的目的是让所有有创业意愿的或者刚刚起步的创业者更好地了解现在中国创业行业的氛围，以及创业初期不得不知的干货。每节课都凝结了真格基金业内专家和北京大学资深教授的丰富知识，也包括多位成功创业者的实践经验。完成本课程之时，你将能够为自己绝妙的创意找到切实可行的商业模式，组建能力出色行动高效的团队，撰写专业具体可执行的策划书。在课程的最后，我们希望所有的学员可以完成一份完整的商业计划书，把自己的创业想法有效地表达出来。成功完成慕课的学生会得到由Coursera提供的真格基金与北京大学联合认证的付费认证书，并且其商业计划书会得到真格基金的认真考虑。
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (420, 'Draft', 'Modern History,Root.History.Modern_History', 'Intermediate', 'Live', 'Understanding China, 1700-2000: A Data Analytic Approach, Part 2', 'The purpose of this course is to summarize new directions in Chinese history and social science produced by the creation and analysis of big historical datasets based on newly opened Chinese archival holdings, and to organize this knowledge in a framework that encourages learning about China in comparative perspective.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (421, 'Active', 'Root,Root', 'Intermediate', 'Offline', 'Image and Video Processing: From Mars to Hollywood with a Stop at the Hospital', 'In this course, you will learn the science behind how digital images and video are made, altered, stored, and used. We will look at the vast world of digital imaging, from how computers and digital cameras form images to how digital special effects are used in Hollywood movies to how the Mars Rover was able to send photographs across millions of miles of space.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (422, 'Active', 'Ancient History,Root.History.Ancient_History', 'Medium', 'Offline', 'The Art of Music Production', 'Explore the art of record production and how to make recordings that other people will love listening to. This course will teach you how to make emotionally moving recordings on almost any recording equipment, including your phone or laptop. The emphasis is on mastering tangible artistic concepts; the gear you use is up to you. You will learn to develop the most important tool in the recording studio: your ears. You will learn to enhance every aspect of your own productions, both sonically and musically, by employing deeper listening skills. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (423, 'Active', 'The Digital Age,Root.History.Modern_History.The_Industrial_Revolution.The_Digital_Age', 'Advanced', 'Live', 'Dimensionality Reduction using an Autoencoder in Python ', 'In this 1-hour long project, you will learn how to generate your own high-dimensional dummy dataset. You will then learn how to preprocess it effectively before training a baseline PCA model. You will learn the theory behind the autoencoder, and how to train one in scikit-learn. You will also learn how to extract the encoder portion of it to reduce dimensionality of your input data. In the course of this project, you will also be exposed to some basic clustering strength metrics.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (424, 'Active', 'Higher Order Differential Equations,Root.Mathematics.Differential_Equations.Higher_Order_Differential_Equations', 'Beginner', 'Live', 'Enseignes et afficheurs à LED', 'Comprendre le fonctionnement des enseignes et des afficheurs à LED, depuis les petites enseignes à motifs fixes jusqu''aux écrans géants à LED.  Apprendre à les fabriquer et à les programmer les microcontrôleurs qui les pilotent.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (446, 'Draft', 'Astrophysics,Root.Science.Astronomy.Astrophysics', 'Medium', 'Live', 'Creativity, Innovation, and Change', 'Let’s keep making history together - over and over!
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (426, 'Inactive', 'The Enlightenment,Root.History.Modern_History.The_Enlightenment', 'Low', 'Live', 'Democracia y decisiones públicas. Introducción al análisis de políticas públicas', '¿Qué son las políticas públicas? Son la plasmación más concreta de la política. Lo que los gobiernos hacen, y dejan de hacer, para hacer frente a los problemas colectivos. Este curso trata sobre cómo se decide qué problemas abordar y qué soluciones adoptar. Un proceso primordialmente político y social que suele escapar al análisis puramente racional.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (427, 'Active', 'Ancient History,Root.History.Ancient_History', 'Absolute Beginner', 'Offline', 'Ethical Social Media ', 'Social media connects us across space and time, allowing us to find like-minded communities and participate creatively in public life as never before.  We may often use social media without thinking much about the possible consequences.   But there is a shadow side associated with social media use, which takes the form of hate speech, increased surveillance, lack of anonymity and questionable use of our data. What do we need to know to use social media spaces effectively, in a way that is safe and productive for all?
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (428, 'Draft', 'Applied Physics,Root.Science.Physics.Applied_Physics', 'High', 'Offline', 'Internet of Things V2: DragonBoard™ bring up and community ecosystem', 'Do you want to develop skills to prototype embedded products using state-of-the-art technologies? In this course you will build a hardware and software development environment to guide your journey through the Internet of Things specialization courses. We will use the DragonBoard™ 410c single board computer (SBC). 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (429, 'Inactive', 'Quantum Mechanics,Root.Science.Physics.Theoretical_Physics.Quantum_Mechanics', 'Absolute Beginner', 'Offline', 'Revisão Sistemática e Meta-análise', 'Este curso introduz um tipo de estudo conhecido por revisão sistemática. Vamos aprender sobre como a revisão sistemática se distingue das demais. Para isso esmiuçaremos cada uma de suas etapas: estruturar perguntas factíveis, organizar buscas por evidências científicas, selecionar artigos de maneira confiável, coletar dados dos artigos selecionados, avaliar a qualidade da evidência disponível, sintetizar o conjunto de evidências, atribuir confiança na síntese e divulgar no ambiente científico. Organizamos o curso em vídeos e textos curtos para ajudá-lo a compreender esses assuntos. O curso dispõe de exercícios e tutoria para orientá-lo da melhor forma.
