INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (430, 'Inactive', 'Physics,Root.Science.Physics', 'Low', 'Live', 'FinTech Law and Policy', 'Being a successful FinTech firm requires more than just great technology; it also requires an understanding of the laws and regulations applicable to your business. This course will provide you with that understanding. You will learn about the critical legal, regulatory, and policy issues associated with cryptocurrencies, initial coin offerings, online lending, new payments and wealth management technologies, and financial account aggregators.  In addition, you will learn how regulatory agencies in the U.S. are continually adjusting to the emergence of new financial technologies and how one specific agency has proposed a path for FinTech firms to become regulated banks. You will also learn the basics of how banks are regulated in the U.S.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (431, 'Draft', 'Late Antiquities,Root.History.Ancient_History.Late_Antiquities', 'Low', 'Live', 'Thoracic Oncology', 'Thoracic malignancies are major, global health problems.  Lung cancer is the most common cancer and cause of cancer death in the world, with more than 1.5 million deaths per year.  More Americans will die from lung cancer each year (approximately 159,480) than from colon, breast, pancreatic, and prostate cancer combined (approximately 158,630), the next most common causes of cancer death.  Esophageal cancer is the 6th most common cause of cancer deaths worldwide, and the 4th most common cause in developing nations.
