INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (463, 'Inactive', 'The Industrial Revolution,Root.History.Modern_History.The_Industrial_Revolution', 'Absolute Beginner', 'Offline', '刑法学总论 Criminal Law', '刑法是规定犯罪与刑罚的法律。刑法学是研究刑法的科学，主要研究犯罪的成立条件以及刑罚的种类与适用刑法的制度等问题。本课程的主要目的是简明完整地说明现代刑法学总论的基本概念、基本理论与基本方法，包括的主要内容有刑法与刑法学的基本概念，刑罚的正当性，罪刑法定原则，刑法解释，刑法的适用范围，犯罪与犯罪构成的概念，犯罪构成的主要内容，排除犯罪的根据，犯罪的未完成状态，共同犯罪，刑罚的概念与体系，量刑制度，数罪并罚，缓刑、减刑与假释。本课程通过理论说明与案例分析对有关问题进行讲解，鼓励同学们通过刑法发展来认识刑法的生命，通过刑法比较来认识刑法的国际标准，通过确立刑法理想来追求社会的公平正义。
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (464, 'Draft', 'Astronomy,Root.Science.Astronomy', 'Introductory', 'Offline', 'Named Entity Recognition using LSTMs with Keras', 'In this 1-hour long project-based course, you will use the Keras API with TensorFlow as its backend to build and train a bidirectional LSTM neural network model to recognize named entities in text data. Named entity recognition models can be used to identify mentions of people, locations, organizations, etc. Named entity recognition is not only a standalone tool for information extraction, but it also an invaluable preprocessing step for many downstream natural language processing applications like machine translation, question answering, and text summarization. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (465, 'Draft', 'Number Theory,Root.Mathematics.Number_Theory', 'Low', 'Online', 'Moses'' Face: Moses'' images as reflected in Jewish literature', 'One was Moses but many are his faces. Starting with the Hebrew Bible, continuing with Jewish literatures throughout history (Second Temple literature, rabbinic literature, medieval commentaries, modern literature, thought and art.) the character of Moses keeps changing and developing through the eyes of the beholder, it''s time, tendency and the nature of the genre. The course will follow the different phases of Moses'' character and try to understand what motives are behind them.
