INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (561, 'Inactive', 'Ordinary Differential Equations,Root.Mathematics.Differential_Equations.Ordinary_Differential_Equations', 'Advanced', 'Offline', 'Introduction to Intellectual Property', 'Intellectual property is the currency of the tech world, with the world’s most valuable intellectual property assets dwarfing the value of their real-world counterparts. Apple’ trademarked brand was valued at over $100 billion as of 2017. J.K. Rowling’s Harry potter franchise has generated over $25 billion to date. But how did the law come to create such enormous value in intangible assets?
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (562, 'Inactive', 'Late Antiquities,Root.History.Ancient_History.Late_Antiquities', 'Introductory', 'Live', 'Smart Cities – Management of Smart Urban Infrastructures', 'Learn about Smart Cities within the context of management of urban infrastructures. The introduction of Smart urban technologies into legacy infrastructures has resulted in numerous challenges and opportunities for contemporary cities and will continue to do so. This course will help you to understand how to make the best of these smart technologies in your cities’ legacy infrastructures.  
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (563, 'Active', 'String Theory,Root.Science.Physics.Theoretical_Physics.String_Theory', 'Beginner', 'Online', 'Google Cloud Fundamentals for Azure Professionals: Core Infrastructure', 'This course introduces you to important concepts and terminology for working with Google Cloud Platform (GCP). You learn about, and compare, many of the computing and storage services available in Google Cloud Platform, including Google App Engine, Google Compute Engine, Google Kubernetes Engine, Google Cloud Storage, Google Cloud SQL, and BigQuery. You learn about important resource and policy management tools, such as the Google Cloud Resource Manager hierarchy and Google Cloud Identity and Access Management. Hands-on labs give you foundational skills for working with GCP.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (564, 'Active', 'Ordinary Differential Equations,Root.Mathematics.Differential_Equations.Ordinary_Differential_Equations', 'Beginner', 'Offline', 'Hacer negocios con multinacionales y empresas en América Latina', 'Acompáñanos en el curso Hacer negocios con multinacionales y empresas en América Latina. En este curso, conocerás las características de las multilatinas más grandes, comprenderás  cómo han construido su ventaja competitiva doméstica e internacional, e identificarás cuáles son los desafíos e innovaciones para lograr sostener su desempeño económico superior.
