INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (566, 'Draft', 'Mathematics,Root.Mathematics', 'Beginner', 'Live', 'Introduction to Public Health Engineering in Humanitarian Contexts', 'The world is facing unprecedented humanitarian needs. Today’s humanitarian crisis tend to be greater in number, often in urban settings, longer in duration and broader in regional impact. They generate human sufferings on a greater scale, disrupt essential services, such as water supply or sanitation and put health of large population at risk. Engineers and technical specialists in water, sanitation, energy, environment, and in other related fields play a vital role to respond to these challenges and growing needs. In the humanitarian sector, they are called the public health engineers and today they are increasingly needed! Why public health engineering matters so much in humanitarian crises? And how its related activities are carrying out in such complex environment? This is what this MOOC is all about!
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (567, 'Draft', 'Physics,Root.Science.Physics', 'Beginner', 'Live', 'Estructuras de datos en Python', 'Este curso te dará ágil acceso a  las estructuras de datos principales del lenguaje de programación Python en su versión 3.0. 
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (568, 'Inactive', 'Multi-variable Calculus,Root.Mathematics.Calculus.Multi_variable_Calculus', 'Low', 'Live', 'Perform Feature Analysis with Yellowbrick', 'Welcome to this project-based course on Performing Feature Analysis with Yellowbrick. In this course, we are going to use visualizations to steer machine learning workflows. The problem we will tackle is to predict whether rooms in apartments are occupied or unoccupied based on passive sensor data such as temperature, humidity, light and CO2 levels. With an emphasis on visual steering of our analysis, we will cover the following topics in our machine learning workflow: feature analysis using methods such as scatter plots, RadViz, parallel coordinates plots, feature ranking, and manifold visualization.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (569, 'Draft', 'Electricity and Magnetism,Root.Science.Physics.Applied_Physics.Electricity_and_Magnetism', 'Absolute Beginner', 'Offline', 'Addiction Treatment: Clinical Skills for Healthcare Providers', 'This course is designed with a singular goal: to improve the care you provide to your patients with substance use disorders. By delving into a model case performed by actors, seven Yale instructors from various fields provide techniques to screen your patients for substance use disorder risk, diagnose patients to gauge the severity of their use, directly manage treatment plans, refer out to treatment services, and navigate the various conditions that may limit your patient’s access to treatment. You will ultimately be prepared to provide compassionate and evidence-based care to a large population of patients living with addiction— a chronic, often relapsing-remitting disease, but a treatable one.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (570, 'Inactive', 'Root,Root', 'Advanced', 'Live', 'Exploring Light: Hands-on Activities and Strategies for Teachers', 'This is an Exploratorium teacher professional development course taught by Teacher Institute staff, open to any science teacher (particularly middle or high school level) and science enthusiast. This is a hands-on workshop that explores topics and strategies teachers can use to help their students become active investigators of light.
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (571, 'Active', 'Differential Equations,Root.Mathematics.Differential_Equations', 'Beginner', 'Online', 'Evaluación para el aprendizaje: Enfoque cuantitativo', 'La perspectiva contemporánea de la educación ha considerado que dentro del proceso educativo es indispensable la evaluación como una parte esencial de la enseñanza  y el aprendizaje. Realizar este proceso de manera adecuada requiere de una formación especializada que nos permita conocer tanto el proceso en sí mismo, así como los elementos involucrados en él y la pertinencia de hacer uso de las técnicas e instrumentos más adecuados y necesarios para cada tipo de evaluación. 
