INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (579, 'Draft', 'Italian Renaissance,Root.History.Modern_History.Italian_Renaissance', 'Beginner', 'Offline', 'Farmacovigilancia ocular', 'En este curso, el participante identificará entre una reacción adversa al medicamento, un evento temporalmente asociado a vacunas y las consecuencias adversas de las tecnologías médicas. Se busca la mejora de la seguridad del paciente al disminuir el riesgo de pérdida de la visión o afecciones oculares secundarias a través de la valoración del caso y la aplicación de técnicas diagnósticas oculares. La farmacovigilancia ocular es una actividad que puede realizar cualquier profesional de la salud, en el entendido de que busca la efectividad del fármaco y el seguimiento de problemas comunes poco identificados en la población, los cuales dependen de las potenciales interacciones medicamentosas (fármaco-fármaco, fármaco-alimento, fármaco-planta medicinal). 
