INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (592, 'Active', 'Late Antiquities,Root.History.Ancient_History.Late_Antiquities', 'Medium', 'Live', 'Data Science for Business Innovation', 'The course is a compendium of the must-have expertise in data science for executive and middle-management to foster data-driven innovation. It consists of introductory lectures spanning big data, machine learning, data valorization and communication. Topics cover the essential concepts and intuitions on data needs, data analysis, machine learning methods, respective pros and cons, and practical applicability issues.	
INSERT INTO public.course (id, status, subject, level, instruction_mode, name, description, requirements, location, num_credits, price, weekly_effort, avatar, learning_objectives, short_name, skills, start_time_dt, end_time_dt, created_dt, updated_dt, _uuid) VALUES (593, 'Inactive', 'The Enlightenment,Root.History.Modern_History.The_Enlightenment', 'Beginner', 'Online', 'The Psychology of Thrill Seekers', 'Sensation seeking is a trait we all have and includes the search for complex and new experiences. Thrill Seekers, people with high-sensation seeking personalities, crave exotic and intense experiences even when physical or social risks are involved. This course helps learners examine the remarkable world of the high-sensation seeking personality and explores the lifestyle, psychology, and neuroscience behind thrill seekers.', '{"Python Functions, Files, and Dictionaries","Advanced Business Strategy","Battery Pack Balancing and Power Estimation","University Teaching","Engagement & Nurture Marketing Strategies","Managing Uncertainty in Marketing Analytics","Input and Interaction","Ebola : Vaincre ensemble !","Essentials in Clinical Simulations Across the Health Professions","Optical Efficiency and Resolution"}', 'Riverdol', 8, 499, 28, 'http://www.outdoor-photos.com/_photo/2569091.jpg', '{"Engrossed rally continent running midnightspout masting lighted toying infallible humour associates","Precisely enough burst forgetting incessantly slow","Plumed stagger drooping spread porter selfcommand entablatures","Flight divine parative threat furred sunsets","Single lessly trivial unvarying drawn floating bleak charlemagne","Elaborately maintaining maledictions","Influence equatorial seizing storm subterranean centaurs"}', 'TPoTS_2k', '{"Violently snort loungingly","Smallheaded stirred","Embark worthy force","Leisure level","Identity sliding","Exile process"}', '10:30:00-06', '14:00:00-06', '2047-06-10 11:06:13+00', '2049-05-06 17:30:17+00', '80790447-6ca8-4115-a1a0-1d19252